  const [emojis, setEmojis] = useState(initialEmojis);
  const [index, setIndex] = useState({ start: 0, end: 3 });

  // Function to shuffle emojis
  const shuffleEmojis = (array) => {
    let currentIndex = array.length, randomIndex;
    while (currentIndex !== 0) {
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex--;
      [array[currentIndex], array[randomIndex]] = [array[randomIndex], array[currentIndex]];
    }
    return array;
  };

  // Effect to shuffle emojis on mount
  useEffect(() => {
    setEmojis(shuffleEmojis([...initialEmojis]));
  }, []);

  // Navigate forward through emojis
  const navigateForward = () => {
    setIndex((prevIndex) => {
      let { start, end } = prevIndex;
      if (end + 3 > emojis.length) {
        start = 0;
        end = 3;
      } else {
        start += 3;
        end += 3;
      }
      return { start, end };
    });
  };
  
  // Navigate backward through emojis
  const navigateBackward = () => {
    setIndex((prevIndex) => {
      let { start, end } = prevIndex;
      if (start - 3 < 0) {
        start = Math.max(0, emojis.length - (emojis.length % 3));
        end = emojis.length;
      } else {
        start -= 3;
        end = start + 3;
      }
      return { start, end };
    });
  };

